class Product {
  // title = "Defaul Title";
  // imageUrl;
  // description;
  constructor(title, imageUrl, description, price) {
    this.title = title;
    this.imageUrl = imageUrl;
    this.description = description;
    this.price = price;
  }
}

class ElementAttribute {
  constructor(attrName, attrValue) {
    this.name = attrName;
    this.value = attrValue;
  }
}

class Component {
  constructor(renderHookId, isRender = true) {
    this.hookId = renderHookId;
    if (isRender) {
      this.render();
    }
  }

  render() {}

  createRootElement(tag, className, attributes) {
    const rootElement = document.createElement(tag);
    if (className) {
      rootElement.className = className;
    }
    if (attributes && attributes.length > 0) {
      for (const attr of attributes) {
        rootElement.setAttribute(attr.name, attr.value);
      }
    }
    document.getElementById(this.hookId).append(rootElement);
    return rootElement;
  }
}

class ShoppingCart extends Component {
  items = [];
  constructor(hookId) {
    super(hookId, false);
    this.orderProducts = () => {
      console.log("Ordering...");
      console.log(this.items);
    };
    this.render();
  }
  addProudct(product) {
    const updatedItmes = [...this.items];
    updatedItmes.push(product);
    this.cartItems = updatedItmes;
  }
  set cartItems(value) {
    this.items = value;
    this.totalOutput.innerHTML = `<h2>Total: \$${this.totalAmount.toFixed(2)}</h2>`;
  }
  get totalAmount() {
    const sum = this.items.reduce((preValue, curItem) => preValue + curItem.price, 0);
    return sum;
  }

  render() {
    const cartEl = this.createRootElement("section", "cart");
    cartEl.innerHTML = `
      <h2>Total: \$${0}</h2>
      <button>Order Now</button>
    `;
    this.totalOutput = cartEl.querySelector("h2");
    const orderButton = cartEl.querySelector("button");
    orderButton.addEventListener("click", () => this.orderProducts());
  }
}

class ProductList extends Component {
  products = [];
  constructor(hookId) {
    super(hookId);
    this.fetchProducts();
  }

  fetchProducts() {
    this.products = [
      new Product(
        "A Pillow",
        "data:image/jpeg;base64,",
        "A soft pillow!",
        19.99
      ),
      new Product(
        "A Carpet",
        "data:image/jpeg;base64,",
        "A carpet which you might like - or not.",
        89.99
      ),
    ];
    this.renderProducts();
  }

  renderProducts() {
    for (const prod of this.products) {
      new ProductItem(prod, "prod-list");
    }
  }

  render() {
    this.createRootElement("ul", "product-list", [new ElementAttribute("id", "prod-list")]);
    if (this.products && this.products.length > 0) {
      this.renderProducts();
    }
  }
}

class ProductItem extends Component {
  constructor(product, hookId) {
    super(hookId, false);
    this.product = product;
    this.render();
  }
  addToCart() {
    App.addProductToCart(this.product);
  }
  render() {
    const prodEl = this.createRootElement("li", "product-item");
    prodEl.innerHTML = `
        <div>
            <img src="${this.product.imageUrl}" alt="${this.product.title}" />
            <div class="product-item__content">
                <h2>${this.product.title}</h2>
                <h3>${this.product.price}</h3>
                <p>${this.product.description}</p>
                <button>Add to Cart</button>
            </div>
        </div>
    `;
    const addCartBtnElement = prodEl.querySelector("button");
    addCartBtnElement.addEventListener("click", this.addToCart.bind(this));
  }
}

class Shop {
  constructor() {
    this.render();
  }
  render() {
    this.cart = new ShoppingCart("app");
    new ProductList("app");
  }
}

class App {
  static cart;
  static init() {
    const shop = new Shop();
    this.cart = shop.cart;
  }

  static addProductToCart(product) {
    this.cart.addProudct(product);
  }
}

App.init();
